    public function getSourceContext()
    {
        return new Source("{% extends 'admin/layout.html.twig' %}

{% block title %}Category index{% endblock %}

{% block body %}
    <h1>Category</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Name</th>
                <th>Enabled</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for category in categories %}
            <tr>
                <td>{{ category.id }}</td>
                <td>{{ category.name }}</td>
                <td>{{ category.enabled ? 'Yes' : 'No' }}</td>
                <td>
                    <a href=\"{{ path('category_show', {'id': category.id}) }}\">show</a>
                    <a href=\"{{ path('category_edit', {'id': category.id}) }}\">edit</a>
                     <a href=\"{{ path('category_change_status', {'id': category.id}) }}\">
                      {{category.enabled ? 'desactiver' : 'activer'}}
                    </a>
                    
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"4\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('category_new') }}\">Create new</a>
{% endblock %}
", "admin/category/index.html.twig", "/opt/lampp/htdocs/blog_symfony/templates/admin/category/index.html.twig");
